for (i = 0; i < count; i++) {
    GLuint texture;
    if (textures == NULL) {
        texture = 0;
    } else {
        texture = textures[i];
    }
    glActiveTexture(GL_TEXTURE0 + first + i);
    if (texture != 0) {
        GLenum target = /* target of textures[i] */;
        glBindTexture(target, textures[i]);
    } else {
        for (target in all supported targets) {
            glBindTexture(target, 0);
        }
    }
}
