namespace OpenTK.Rewrite
{
    /// <summary>
    /// Provides constants representing attribute names which are used in the rewriting process.
    /// </summary>
    public static class AttributeNames
    {
        /// <summary>
        /// Specifies the name of the AutoGeneratedAttribute.
        /// </summary>
        public const string AutoGenerated = "AutoGeneratedAttribute";

        /// <summary>
        /// Specifies the name of the CountAttribute.
        /// </summary>
        public const string Count = "CountAttribute";

        /// <summary>
        /// Specifies the name of the RewrittenAttribute.
        /// </summary>
        public const string Rewritten = "RewrittenAttribute";

        /// <summary>
        /// Specifies the name of the SlotAttribute.
        /// </summary>
        public const string Slot = "SlotAttribute";
    }
}
